mapper.select:
select pc.name as categoryName, pa.id, pa.ext_id as extId, pa.publish_date as publishDate, pa.deleted, pa.category_ext_id as categoryExtId, pa.category_id as categoryId, pa.is_publish as isPublish, pa.is_need_update as isNeedUpdate, pa.is_top as isTop, pa.title, pa.source, pa.link, pa.description, pa.thumbnail_id as thumbnailId, pa.is_focus as isFocus, pa.content, pa.level, pa.created_at as createdAt, pa.updated_at as updatedAt from po_draft_article pa left join po_category pc on pa.category_id = pc.id where 1 = 1
and pa.category_id = #{categoryId,jdbcType=BIGINT}
and (pa.title like concat(concat('%',#{keyword,jdbcType=VARCHAR}),'%') or pa.content like concat(concat('%',#{keyword,jdbcType=VARCHAR}),'%'))
